// 입력 데이터 처리
const input = $input.first().json.body || $input.first().json;

const claimId = `CLM-${Date.now()}`;
const totalAmount = 1500000;
const approvedAmount = Math.round(totalAmount * 0.9) + 1000000;

return [{
  json: {
    success: true,
    claim_case_id: claimId,
    policy_id: input.policy_id || 'POL-2024-001',
    customer_name: input.customer_name || '홍길동',
    status: 'approved',
    ocr_result: {
      diagnosis_name: '급성충수염',
      diagnosis_code: 'K35.0',
      treatment_type: '입원',
      total_amount: totalAmount
    },
    review_result: {
      total_claimed: totalAmount,
      total_approved: approvedAmount,
      total_rejected: Math.round(totalAmount * 0.1),
      decision: '질병입원의료비 90% + 수술비 정액 지급 승인'
    },
    processed_at: new Date().toISOString()
  }
}];
